def process_clientKeyCredential(client_key_credential, server_Signature):
    # This function will return a value of True if the client key credential is valid
    # If the client key credential is invalid, it will return a value of False
    try:
        # Get a byte string of server signature and return
        signed_value = client_key_credential.Validate(server_Signature)
    except:
        signed_value = b""
    # Compare the byte string with the server signature and return value of True if they match
    # Else return False
    if signed_value:
        return True
    return False


