def validate_Password(password_Info):
    """
    This function takes in password information and returns whether it is valid.
    A password must be at least 8 characters long, contain at least one uppercase letter, one lowercase letter, and one digit.
    """

    # Password must be at least 8 characters long.
    if len(password_Info) < 8:
        return False
    else:
        has_upper = False
        has_lower = False
        has_digit = False

        # Check if the password contains at least one uppercase letter, one lowercase letter, and one digit.
        for char in password_Info:
            if char.isupper():
                has_upper = True
            elif char.islower():
                has_lower = True
            elif char.isdigit():
                has_digit = True

    # If all conditions are met, return True.
    if has_upper and has_lower and has_digit:
        return True
    else:
        return False

