def process_valid_IPAddress(IP_address):
    # check if the IP address is valid
    try:
        IP_address_ipaddress = IPAddress(IP_address)
    except ValueError:
        return False
    
    # check if the IP address is in the valid range
    if IP_address_ipaddress.netmask == IPAddress('255.255.255.255'):
        return True
    else:
        return False
    
