def retrieve_credentials(user_Data):
    credentials = {}
    for user in user_Data:
        user_Data = user.replace('\n', '')
        username = user_Data.split('\n')[0]
        password = user_Data.split('\n')[1]
        credentials[username] = password
    return credentials

def retrieve_user_data(user_Data):
    users = {}
    for user in user_Data:
        user_Data = user.replace('\n', '')
        username = user_Data.split('\n')[0]
        user_id = user_Data.split('\n')[1]
        email = user_Data.split('\n')[2]
        phone = user_Data.split('\n')[3]
        address = user_Data.split('\n')[4]
        users[username] = {"ID": user_id, "Email": email, "Phone": phone, "Address": address}
    return users

