def check_CPF(user_CPF):
    user_CPF = str(user_CPF)
    if len(user_CPF) != 11:
        return False
    i = 0
    sum = 0
    for digito in user_CPF:
        sum += int(digito) * i
        i +=1

    return sum % 11 == 0

