def get_tokens_in_request(req):
    # get authentication credentials from servlet request for example
    auth_header_ = req.headers.get('Authorization')
    auth_credential = auth_header_.split(' ')[1]
    tokens = auth_credential.split(':')
    realm = tokens.pop(0)
    token = tokens.pop(0)
    return realm, token

