    try:
        mac_Address = mac_Address.strip("\n:")
        mac_Address = mac_Address.upper()
        x = mac_Address.split(":")

        if len(x) != 6:
            raise ValueError

        for i in range(0, len(x)):
            try:
                int(x[i], 16)
            except:
                raise ValueError
    except:
        return False
    else:
        return x


def validate_IPv4_Address(ipAddress):
    try:
        ipAddress = ipAddress.strip("\n.")
        ipAddress = ipAddress.split(".")
        if ipAddress.count(".") == 3:
            for i in range(0, 4):
                if int(ipAddress[i]) < 0 or int(ipAddress[i]) > 255:
                    raise ValueError
        else:
            raise ValueError
    except:
        return False
    else:
        return ipAddress


def validate_IPv6_Address(ipAddress):
    try:
        ipAddress = ipAddress.strip("\n:")
        ipAddress = ipAddress.split(":")
        if ipAddress.count(":") == 7:
            x = 0
            for i in range(0, len(ipAddress)):
                if len(ipAddress[i]) == 0:
                    raise ValueError
                if len(ipAddress[i]) > 4:
                    raise ValueError
                for j in ipAddress[i]:
                    if not(j.isdigit() or j.isalpha()):
                        raise ValueError
                x += 1
        else:
            raise ValueError
    except:
        return False
    else:
        return ipAddress


