                return [1, "", f"Hostname validation failed for system {system}"]
        return [2, "", f"IP address for system {system} is valid. Hostname {system_name} is valid"]
    else:
        return [3, f"System {system} is not found in the system_name_details map", '']

    info_list = system_IPAddress_Info.split('.')
