# A decorator which takes a list of arguments
def add_port_number(port_number):

    # A wrapper function
    def decorate(func):
        # A function which takes some argument
        def modified_straight_argument(*args, **kwargs):
            # Call the original function and pass on the arguments
            return func(args[0], args[1], port_number)

        return modified_straight_argument

    return decorate


