def check_adjacent_bits(user_input):
    input = int(input("Please enter the number of bits you would like to check (max 32): "))
    bits = input
    check_list = []
    while input > 0:
        check_list.append(input % 2)
        input = input // 2
    check_list.append(0)
    print(check_list)
    for x in check_list:
        if x == 0 and check_list.pop(0) == 1:
            print('Adjacent bits found')
            break
        elif x == 0 and check_list.pop(0) == 0:
            print('No adjacent bits found')
            break

def check_binary_palindrome(user_input):
    input = int(input("Please enter the number of bits you would like to check (max 32): "))
    bits = input
    check_list = []
    while input > 0:
        check_list.append(input % 2)
        input = input // 2
    check_list.append(0)
    print(check_list)
    if check_list == check_list[::-1]:
        print('This binary number is a palindrome')
