def get_Password(courses):
    '''The function gets the student's password'''
    password = [''] * len(courses)
    count = ''
    for i in range(len(courses)):
        count += str(courses[i][1])
    for i in range(0, len(count), 5):
        count = count[:i] + '#' + count[i:]
    count = count.split('#')
    for i in range(len(count)):
        count[i] = int(count[i])

    for i in range(len(count)):
        count[i] = hex(count[i])[2:].upper()
    for i in range(len(count)):
        count[i] = '0' + count[i] if len(count[i]) % 2 == 1 else count[i]
    for i in range(len(count)):
        count[i] = '0x'+count[i]+'x' if i != len(count)-1 else '0x'+count[i]
    for i in range(len(count)):
        if i == 0:
            password[i] = input("Enter 1st Password:")
        elif i == 1:
            password[i] = input("Enter 2nd Password:")
        elif i == 2:
            password[i] = input("Enter 3rd Password:")
        elif i == 3:
            password[i] = input("Enter 4th Password:")
        elif i == 4:
            password[i] = input("Enter 5th Password:")
    for i in range(len(password)):
        for j in range(len(password[i])):
            password[i] = password[i].replace(password[i][j], count[i])
    return password


