def validate_IPAddress(IPaddress):
    # check for IPv4
    if IPaddress.startswith("IPv4:"):
        IPv4 = IPaddress[5:]
        try:
            octets = IPv4.split(".")
            if len(octets) != 4:
                return False
        except ValueError:
            return False
        for octet in octets:
            if not isinstance(int(octet), int):
                return False
            if not 0 <= int(octet) <= 255:
                return False
        return True
    # check for IPv6
    elif IPaddress.startswith("IPv6:"):
        IPv6 = IPaddress[5:]
        try:
            octets = IPv6.split(":")
            if len(octets) > 8:
                return False
        except ValueError:
            return False
        for octet in octets:
            if not isinstance(int(octet, 16), int):
                return False
            if len(octet) > 4:
                return False
            if not 0 <= int(octet, 16) <= 65535:
                return False
        return True
    # check for invalid IP address
    else:
        return False


