def json2str(json_obj):
    if isinstance(json_obj, str):
        return json_obj

    elif isinstance(json_obj, dict):
        return convert_dict_to_json_str(json_obj)

    elif isinstance(json_obj, list):
        return convert_list_to_json_str(json_obj)

    else:
        return str(json_obj)

def convert_list_to_json_str(json_obj):
    json_str_list = []
    for inner_obj in json_obj:
        json_str_list.append(convert_dict_to_json_str(inner_obj))

    return json_str_list

def convert_dict_to_json_str(json_obj):
    for key, value in json_obj.items():
        json_obj[key] = json2str(value)
    return json_obj

