'''
Args:
    user_EmailAddress_Details (list): email_address_Details_Details
    client_IPAddress_Details (list): ipaddress_Details_Details

Returns:
    Dict: a dict of
        emails : local emails_and_ip_address, emails_and_ip_address, emails_and_ip_address
        ip_address : ip_address
'''
emails = []
ip_address = []
for email_address in user_EmailAddress_Details:
    '''
    Args:
        email_address (str): email address.
    '''
    is_valid_email = True
    if not email_address:
        is_valid_email = False
    if email_address.startswith("@") or email_address.endswith("@"):
        is_valid_email = False
    if is_valid_email:
        if email_address.count("@") != 1:
            is_valid_email = False
        else:
            main_part = email_address.split("@")[0]
            if main_part[0] == ' ':
                is_valid_email = False
            if not main_part[0].isalnum():
                is_valid_email = False
            if "@" not in main_part:
                is_valid_email = False
            for char in main_part:
                if char == '.':
                    pass
                else:
                    if not char.isalnum():
                        is_valid_email = False
            result = main_part.split(".")
            for part in result:
                if not part:
                    is_valid_email = False
            if is_valid_email:
                email_address = email_address.replace(" ", "")
                emails.append(email_address)

for ip_address in client_IPAddress_Details:
    '''
    Args:
        email_address (str): ip address.
    '''
    if ip_address == "localhost":
        ip_address.append(ip_address)
        continue
    if ":" in ip_address:
        split_ip_address = ip_address.split(":")[0]
        if split_ip_address != "127.0.0.1":
            ip_address.append(ip_address)
        continue
    else:
        if ip_address.startswith("0x" or "0x"):
            pass
        else:
            if not ip_address.startswith("192.168." or "127.0.0.1"):
                ip_address.append(ip_address)
return {
    'emails': emails,
    'ip_address': ip_address,
}

