def get_BetterPassword_Generation(password_Generation, lower_cased, upper_cased, numbers, symbols):
    for i in range(int(password_Generation)):
        if lower_cased:
            result_1 = ''.join(random.choice(string.ascii_lowercase) for j in range(int(password_Generation)))
            return result_1
        if upper_cased:
            result_2 = ''.join(random.choice(string.ascii_uppercase) for j in range(int(password_Generation)))
            return result_2
        if numbers:
            result_3 = ''.join(random.choice(string.digits) for j in range(int(password_Generation)))
            return result_3
        if symbols:
            result_4 = ''.join(random.choice(string.punctuation) for j in range(int(password_Generation)))
            return result_4
        else:
            return '******'


