            if char in guesses:
                print(f"{char}", end=" ")
            else:
                print(" _ ", end=" ")
        guess = input("\n\nTry a letter: ").lower()

        if guess not in resource:
            lives -= 1
            if lives == 0:
                end_of_game = True
                print("You Lose")
            else:
                print(f"You have {lives} lives left")
        else:
            for index in range(len(resource)):
