def process_stringToIP(address):
    # return None if the input is not a string
    if type(address) != str:
        return None
    # an IPv6 address is prepended by :::
    if address.startswith(':::'):
        # use IPv6 raw format
        ip_address = IPv6Address(address[3:])
    # an IPv4 address is prepended by 0:
    else:
        try:
            # use IPv4 raw format
            ip_address = IPv4Address(address[2:])
            return ip_address
        except ValueError:
            # use IPv6 raw format
            ip_address = IPv6Address(address[3:])
    # return the full formatted IP Address
    return ip_address


def process_stringToPort(port):
    # return None if the input is not a string
    if type(port) != str:
        return None
    # a port with no padding is prepended by 0:
    try:
        # use raw port format
        port_int = int(port[2:])
    except ValueError:
        # use raw port format
        port_int = int(port[1:])
    # return the full formatted Port
    return port_int


