def get_DB_Username():
    try:
        import sqlite3 as sql
        sqliteConnection = sql.connect("./database/Database.sqlite")
        cursor = sqliteConnection.cursor()
        select_Query = "SELECT * FROM Users"
        cursor.execute(select_Query)
        sqlite_Username_List = cursor.fetchall()
        sqliteConnection.commit()
        cursor.close()
        sqliteConnection.close()

    except sql.Error as error:
        print("Failed to get to SQLite")
        print(error)

    return sqlite_Username_List


